pragma solidity ^0.4.18;


contract SimpleTokenI {
	function transferFrom(address _from, address _to, uint256 _amount) public returns (bool) {}
}


contract SIMDepositRelease {

	SimpleTokenI st;
	address storageAddress;
	uint256 balance;
    mapping( address => uint256 ) log;

    function() payable {

    }

    function SIMDepositRelease(address _st, address _storageAddress) {
    	st = SimpleTokenI(_st);
    	storageAddress = _storageAddress;
    }

    function callTransferFrom(address _sender, address _receiver, uint256 _amount) {
    	st.transferFrom(_sender, _receiver, _amount);
    	// st.call(bytes4(keccak256("transferFrom(address,address,uint256)")), _sender, _receiver, _amount);
    }

	function deposit(uint256 _amount) public payable {

        var amount = _amount * 10 ** 18;
        callTransferFrom(msg.sender, storageAddress, amount);

        log[msg.sender] += amount;
        balance += amount;

	}

	function transfer(address _sender, address _receiver, uint256 _amount) public payable {

		var amount = _amount * 10 ** 18;
		require(log[_sender] > 0 || log[_sender] >= amount);

		log[_sender] -= amount; 
		balance -= amount;
		callTransferFrom(storageAddress, _receiver, amount);
	}
}
